import { Component, OnInit } from '@angular/core';
import { Row } from './data-table/data-table.component'

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  url = "data:image/svg+xml;base64,"; 
  title = 'Hello World!';
  table =  new Table(["Ten", "Tuoi"], []);
  data = [new Row(["Nguyen Hong Gam", "16"]), new Row(["Nguyen Thanh Hang", "16"]), new Row(["Nguyen Hong Thuy", "15"])]
  searchResult = [];
  ngOnInit(){
    this.url = "data:image/svg+xml;base64,"; 
    this.table.value = this.data;
  }

  search(searchText: string){
    this.searchResult = [];
    this.data.forEach(row => {
      for (let i = 0; i< row.value.length; i++) {
        if (row.value[i] == searchText){
          row.isFound = true;
          break;
        }
      }
      this.searchResult.push(row);
    });

    this.table.value = this.searchResult;
  }
}

export class Table{
  constructor(header: string[], value: Row[]){
    this.header = header;
    this.value = value;
  }
  header: string[];
  value: Row[]
}
